INSERT INTO TextContents(id, language, textvalue) VALUES (373,'pt-PT', 'Sobre a defininição do que é um requisito;\n\na diferença entre um requisito do produto e um requisito do processo;\n\na diferença entre requisitos funcionais e não-funcionais;\n\nrequisitos inter-dependentes;\n\ncomo classificar quantitativamente requisitos;\n\ndiferença entre requisitos de sistema e de software.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (374,'pt-PT', 'Sobre a natureza contínua do processo de requisitos e a sua natureza moldável ao contexto do projecto ou organização;\n\nos diferentes actores do processos (frequentemente conhecidos como “stakeholders”);\n\ncomo gerir um processo de requisitos e como avaliar a sua qualidade e ainda como o melhorar.');
INSERT INTO TextContents(id, language, textvalue) VALUES (375,'pt-PT', 'Sobre a origem dos requisitos (como Conhecimento próprio do Domínio, stakeholders, regras do negócio, etc.) e as técnicas para os coleccionar eficazmente (tais como entrevistas, cenários, protótipos, reuniões, etc.).');
INSERT INTO TextContents(id, language, textvalue) VALUES (376,'pt-PT', 'Sobre como classificar requisitos quanto ao seu tipo, importância, estabilidade ou extensão;\n\nmodelação conceptual (tal como “Use Cases”, modelos de dados, modelos de estados, etc.).');
INSERT INTO TextContents(id, language, textvalue) VALUES (377,'pt-PT', 'Sobre a produção de um conjunto de documentos sobre os requisitos que possa ser sistematicamente revisto, avaliado e aprovado. Estes documentos são: a Definição do Sistema, Especificação dos Requisitos do Sistema e Especificação dos Requisitos do Software (derivados dos requisitos do sistema).');
INSERT INTO TextContents(id, language, textvalue) VALUES (378,'pt-PT', 'Sobre as técnicas de validação dos requisitos levantados, tais como: Revisão por terceiros (“peer-review”), prototipagem, validação de modelos e testes de aceitação.');
INSERT INTO TextContents(id, language, textvalue) VALUES (379,'pt-PT', 'Sobre as particularidades práticas dos requisitos;\n\na natureza iterativa do processo de requisitos;\n\na gestão de alterações dos requisitos (como um todo ou em apenas alguns dos seus atributos);\n\nrastreamento de requisitos;\n\nmedição do peso dos requisitos na eventualidade de alteração do produto.');
INSERT INTO TextContents(id, language, textvalue) VALUES (380,'pt-PT', 'Sobre os conceitos gerais de desenho (como objectivo, restrições, alternativas, contexto etc.);\n\no processo de desenho (diferença entre desenho arquitectural e detalhado);\n\nos principios do desenho de software (como abstracção, acoplamento, coesão, decomposição, modularização, encapsulamento, separação de interface e implementação, suficiência, completividade, etc.).'); 
INSERT INTO TextContents(id, language, textvalue) VALUES (381,'pt-PT', 'Sobre os problemas típicos no desenho de software como concorrência: controlo de eventos;\n\npersistência de dados;\n\ndistribuição de componentes e comunicação entre si;\n\ntratamento de erros e tolerância a falhas;\n\ninteracção e apresentação;\n\nsegurança.'); 
INSERT INTO TextContents(id, language, textvalue) VALUES (382,'pt-PT', 'Sobre os modos de organizar o software a vários níveis de abstracção desde a macro-arquitectura até ao desenho detalhado do código;\n\nos diferentes estilos de arquitectura tais como construção por camadas, sistemas distribuídos (Cliente-Servidor, Three-Tiers, Broker), sistemas interactivos (Model-View-Controller, Presentation-Abstraction-Control), sistemas adaptáveis (microkernel ou reflexão) e outros;\n\nDesign Patterns (como “builder”, “factory”, “adapter”; “bridge”, “iterator”, “strategy”, etc.);\n\no cariz decisório do processo de design;\n\nfamilías de programas e “frameworks” de software.'); 
INSERT INTO TextContents(id, language, textvalue) VALUES (383,'pt-PT', 'Sobre a eficaz comunicação com o utilizador do software;\n\nprincípios base de UI (facilidade de aprendizagem, familiaridade, consistência, surpresa mínima, diversidade de utilização, etc.);\n\nlidar com problemas comuns (interacção com utilizador e  apresentação de informação);\n\nas modalidades típicas de interacção (pergunta-resposta, manipulação directa, selecção em menus, formulários, linguagens de comandos, linguagem natural);\n\ncomo apresentar a informação (número de cores, mudanças de cores, “colour-coding”, visualização para pessoas com necessidades especiais);\n\no processo de design de UI (análise de utilização, prototipagem de software, avaliação da interface com o utilizador desenvolvida);\n\nadaptar a UI para localização e internacionalização;\n\nmetáforas e modelos conceptuais (tal como utilização de ícones reconhecidos universalmente para diversas acções comuns, tal como usar uma roldana para representar as definições).'); 
INSERT INTO TextContents(id, language, textvalue) VALUES (384,'pt-PT', 'Sobre os atributos de qualidade do desenho (tais como portabilidade, testabilidade, robustez, performance, segurança, reutilizável, etc.);\n\ntécnicas de avaliação de qualidade (como revista de design por terceiros, análise estática formal – com modelos matemáticos -  ou informal, simulação ou prototipagem);\n\nmedidas de software para o seu tamanho, estrutura ou qualidade.'); 
INSERT INTO TextContents(id, language, textvalue) VALUES (385,'pt-PT', 'Sobre a representação do desenho em diferentes artefactos universalmente compreendidos;\n\ndescrições de estrutura (diagramas de classe e objectos, diagramas de componentes, “class responsability cards”, diagramas de Entidade-Relacionamento, linguagens de descrição de interfaces, etc.);\n\ndescrições de comportamentos e dinâmicas entre componentes (diagramas de actividade, diagramas de comunicação, diagramas de fluxo de dados, tabelas e diagramas de decisão, diagramas de sequência, diagramas de transição de estados, pseudo-código, etc.).'); 
INSERT INTO TextContents(id, language, textvalue) VALUES (386,'pt-PT', 'Sobre como desenhar um sistema num ou mais paradigmas bem definidos, como Orientado a Funções, Orientado a Objectos, Centrado em Estruturas de Dados e Centrado em Componentes;\n\nas estratégias gerais de desenho como “divide-and-conquer”, “top-down”, “bottom-up”, etc.'); 
INSERT INTO TextContents(id, language, textvalue) VALUES (387,'pt-PT', 'Sobre ferramentas de ajuda à criação de artefactos do desenho de software, tipicamente também software, que consiga, por exemplo, criar modelos e diagramas ou possibilite uma análise de qualidade de modelos.'); 
INSERT INTO TextContents(id, language, textvalue) VALUES (388,'pt-PT', 'Sobre os conceitos fundamentais da construção de software, que são: Minimização de Complexidade, Antecipação de Mudanças, Construção para Verificação, Reutilização e Standards de Construção (relativo às convenções das linguagens de programação usadas, tal como nomenclatura de variáveis ou identação, etc.).' ); 
INSERT INTO TextContents(id, language, textvalue) VALUES (389,'pt-PT', 'Sobre como gerir um processo de construção através de Modelos de Ciclo de Vida (como as metodologias lineares estilo Waterfall ou iterativas tipo Agile);\n\no planeamento da construção (qual a ordem de actividades num processo de construção – isto é altamente dependente de que Modelo de construção é usado);\n\ncomo medir o que é criado (estatísticas sobre código, calendarização, esforço – novamente diferentes medidas dependem do Modelo escolhido, e.g. esforço é uma medida típica da metodologia scrum).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (390,'pt-PT', 'Sobre o cariz predominantemente prático da construção;\n\na necessidade de fazer ajustamentos ou modificações de pequena escala ao Desenho durante a construção;\n\nas linguagens necessárias para construir software - como linguagens de configuração, linguagens de scripting, linguagens de programação, linguagens visuais, etc.;\n\nas características da codificação em si (construções semânticas, estruturas de controlo, tratamento de erros, organização do código em classes, modos de documentação do código, etc.);\n\ntestes de construção (testes unitários e testes de integração);\n\ntécnicas de construção para reutilização por terceiros (tais como estruturação de bibliotecas, utilização de padrões de desenho conhecidos);\n\nreutilização de software pré-existente (recurso a bibliotecas, repositórios públicos, etc.);\n\nqualidade na construção (testes de código, debugging, assertions, programação defensiva, code reviews, etc.);\n\nintegração do código num sistema (phase integration e incremental).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (391,'pt-PT', 'Sobre como desenhar e usar APIs;\n\nproblemas típicos de paradigmas orientados aos objectos (como polimorfismo e reflexão);\n\ntipos parametrizados e genéricos;\n\nassertions, programação por interfaces e programação defensiva;\n\ntratamento de erros;\n\nmodelos executáveis (criação de um programa compilando um modelo executável de uma linguagem de modelação);\n\nprogramação baseada em estados e baseada em tabelas;\n\nconfiguração em runtime e internacionalização;\n\nprocessamento de gramáticas de linguagens;\n\nprimitivas de concorrência (semáforos, mutexes, monitores, etc.);\n\nmiddleware (software que providencia serviços entre a camada do SO e a camada aplicacional);\n\nprogramação para sistemas distribuídos;\n\nconstrução de sistemas heterogéneos (como Processadores de Sinais Digitais ou Sistemas Embebidos);\n\nanálise de performance e tuning (escolhas de arquitecturas, estruturas de dados ou selecção de algoritmos);\n\nstandards de plataformas (por exemplo J2EE vs J2SE);\n\ntest-driven development.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (392,'pt-PT', 'Sobre todo o tipo de ferramentas (tipicamente também software) que ajudam à construção de software tais como IDEs (ambientes de desenvolvimento integrado como Eclipse, Netbeans, Visual Studio, etc);\n\nconstrutores de Interfaces Gráficas (actualmente também parte de IDEs modernos);\n\nferramentas de Testes Unitários e ainda ferramentas de profiling de código, análise de performance e slicing.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (393,'pt-PT', 'Sobre os conceitos fundamentais de teste;\n\ndistinção entre falta, falha, erro e defeito;\n\nescolha de casos de teste;\n\neficácia e objectivos do teste;\n\nteste para descoberta de defeitos;\n\nlimites teóricos e práticos dos testes;\n\ncenários de teste impraticáveis e o relacionamento do teste com as outras actividades do ciclo de vida do software.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (394,'pt-PT', 'Sobre os diferentes níveis a que um software pode ser testado;\n\nquanto ao alvo do teste (testes unitários vs testes de integração vs testes do sistema);\n\nquanto ao objectivo do teste (testes de aceitação,  testes de instalação, testes alfa e beta;\n\ntestes de confiabilidade, testes de regressão, testes de performance, testes de segurança, testes de stress, testes back-to-back, testes de recuperação, testes de interface, testes de configuração e testes de usabilidade).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (395,'pt-PT', 'Sobre as diferentes técnicas usadas para encontrar o maior número de falhas possíveis;\n\ntécnicas baseadas na Intuição do Engenheiro (ad hoc e testes exploratórios);\n\ntécnicas baseadas no domínio do input (particionamento equivalente, teste combinatório, teste de valores-limite, testes aleatórios);\n\ntécnicas baseadas em código (testes baseados nos fluxos de controlo do código, testes do fluxo de dados);\n\ntécnicas baseadas nas falhas (error guessing, testes de mutação);\n\ntécnicas baseadas no uso (teste ao típico perfil operacional, heurísticas de observação do utilizador);\n\ntécnicas baseadas em modelos (tabelas de decisão, máquinas de estado;\n\nespecificações formais, modelos de workflow);\n\ntécnicas baseadas na natureza da aplicação (aplicação web vs aplicação mobile, etc.);\n\ncombinação das técnicas descritas.');
INSERT INTO TextContents(id, language, textvalue) VALUES (396,'pt-PT', 'Sobre as diferentes métricas usadas para avaliar a qualidade dos testes;\n\nmétricas de avaliação do programa (métricas de tamanho, falhas típicas para esse tipo de software, densidade das falhas, avaliação da confiabilidade, modelos de crescimento da confiabilidade);\n\nmétricas de avaliação dos testes realizados (cobertura total de testes, introdução de falhas, resultados das mutações).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (397,'pt-PT', 'Sobre a forma de organizar as actividades, estratégias e técnicas de teste num processo controlado;\n\nconsiderações práticas (criação de guias de teste, organizadores do processo, criação de documentação de testes, test-driven development, criação de uma equipa de testes independente ou interna, estivativas de esforço e teste, definição de terminação de um testes, reutilização de testes e utilização de um padrão de testes);\n\nactividades de teste ( planeamento de teste, geração de casos de teste, desenvolvimento de ambientes de treino, execução sistemática de testes, avaliação do resultado dos mesmos, registo dos resultados ao longo do tempo, rastreamento de defeitos).');
INSERT INTO TextContents(id, language, textvalue) VALUES (398,'pt-PT', 'Sobre as ferramentas, tipicamente também software, que servem de auxilio à escolha realização e avaliação de testes.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (399,'pt-PT', 'Sobre os conceitos básicos, terminologia e definições de manutenção de software;\n\no papel específico de um engenheiro responsável por manutenção de software;\n\nas necessidades de manutenção (corrigir falhas, melhorar o desenho do software, adaptar a novos ambientes, etc.);\n\ncustos de manutenção do software;\n\nevolução do software como uma das finalidades de manutenção;\n\nas diferentes categorias de manutenção (correctiva, adaptativa, perfectiva e preventiva).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (400,'pt-PT', 'Sobre os principais problemas que um responsável pela manutenção de software enfrenta tais como questões técnicas (compreensão limitada do software em questão, testes a um sistema em manutenção, análise de impacto da manutenção proposta, grau de modificabilidade de um sistema em produção);\n\nquestões relacionadas com a gestão do processo de manutenção (alinhar o processo de manutenção de um software com os objectivos da organização responsável, gerir e motivar responsáveis pela manutenção, outsourcing de manutenção);\n\nestimativas de custo de manutenção;\n\nmétricas de manutenção.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (401,'pt-PT', 'Sobre um ciclo de vida de uma fase de manutenção (analisar problemas, implementar as modificações, aprovar, descontinuar o produto, migrar, etc.);\n\nas actividades exclusivas de um engenheiro responsável pela manutenção (análise do estado do software, tarefas de suporte, planeamento da manutenção em termos de prazos e âmbito da manutenção, gestão da configuração do software em manutenção, garantir a qualidade do software em manutenção).');
INSERT INTO TextContents(id, language, textvalue) VALUES (402,'pt-PT', 'Sobre as técnicas utilizadas para manter e/ou actualizar um software com sucesso;\n\ncompreensão do código do software;\n\ntécnicas de alteração sistemática do software (como refactoring);\n\ntécnicas de engenharia reversa de um software;\n\ntécnicas de migração de software;\n\ntécnicas e políticas de descontinuação de software.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (403,'pt-PT', 'Sobre as ferramentas que auxiliam à manutenção de software, tais como ferramentas de corte (slicing) de software (que isolam as partes a modificar), analisadores estáticos e dinâmicos de código;\n\nanalisadores de fluxos de dados e de dependências e ainda ferramentas de auxílio à engenharia reversa.');
INSERT INTO TextContents(id, language, textvalue) VALUES (404,'pt-PT', 'Sobre como organizar uma gestão de configurações;\n\nquais as restrições impostas ao processo;\n\nsobre o planeamento típico de um processo de gestão de configurações (quem é o responsável, qual a calendarização do projecto, quais as ferramentas a ser usadas, que configurações providenciar a terceiros, as interacções potenciais entre o software a ser controlado e externos);\n\ndocumentação das configurações e sua gestão e utilização do mesmo como guidelines numa organização;\n\ngarantia da aplicação correcta da configurações da organização seja através da aplicação de métricas, ferramentas próprias ou simples auditorias.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (405,'pt-PT', 'obre a identificação das entidades a serem sujeitas a controlo de configurações, quais os relacionamentos entre si, como definir o seu versionamento, regras de criação de uma baseline, políticas de integração de um item de software (um ficheiro, código-fonte, recursos visuais, etc) no controlo de configurações;\n\nutilização e gestão de software libraries.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (406,'pt-PT', 'Sobre o controlo do fluxo normal de um processo de gestão de alterações na configuração do software, como requisição de alteração (vulgo change request), avaliação e aceitação/rejeição da alteração proposta;\n\nimplementação da alteração e seu rastreamento e por fim autorizações de desvio (da funcionalidade a alterar) ou renúncia (idem).');
INSERT INTO TextContents(id, language, textvalue) VALUES (407,'pt-PT', 'Sobre o registo e relato da evolução do estado das configurações de software ao longo do tempo;\n\ncomo capturar o estado de itens de software a cada momento e como reportá-los à organização.');
INSERT INTO TextContents(id, language, textvalue) VALUES (408,'pt-PT', 'Sobre o processo de escrutinar as configurações de software de modo a garantir que o seu funcionamento está alinhado com os requisitos especificado (como um todo ou a nível de determinada baseline), a arquitectura/desenho planeada e se está alinhado com a documentação.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (409,'pt-PT', 'Sobre como organizar a entrega de uma ou mais versões de software;\n\ncomo executar um processo de build de software (empacotamento, distribuição de diferentes versões em simultâneo, diferentes ambientes de produção;\n\na entrega de um produto de software completo (quanto efectuar a entrega com base no estado do software, quais os restantes artefactos entregáveis – como documentação e notas de entrega).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (410,'pt-PT', 'Sobre os tipos de ferramentas utilizados para suportarem a eficaz gestão de configurações, tais como ferramentas de controlo de versões, ferramentas de build, ferramentas de suporte a problemas de clientes, ferramentas de controlo de workspaces para equipas de desenvolvimento, ferramentas de issue-tracking, etc.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (411,'pt-PT', 'Sobre como determinar e negociar requisitos de software;\n\nanálise de viabilidade (avaliação da capacidade de realizar os requisitos com base em todas as restrições a que está sujeito - como custos, tecnologias, recursos humanos, políticas, etc.);\n\nprocessos de revisão de requisitos (quais as regras acordadas com stakeholders para fazer eventuais alterações aos requisitos).');
INSERT INTO TextContents(id, language, textvalue) VALUES (412,'pt-PT', 'Sobre como planear um projecto de software;\n\nqual o modelo de ciclo de vida de software a usar (tal como cascata, espiral ou métodos ágeis) e que ferramentas a usar para garantir a sua aplicação;\n\nescolha dos artefactos entregáveis (documentação, software instalável, etc.);\n\nestimação de custos, esforço e temporização;\n\nalocação de recursos humanos a tarefas;\n\ngestão de risco (identificação, análise de impacto, prioritização de factores de risco, desenvolvimento de estratégias de atenuação de risco, etc.);\n\ngestão da qualidade (definição de métricas de qualidade, garantir a aplicação de controlo de qualidade durante o projecto).');
INSERT INTO TextContents(id, language, textvalue) VALUES (413,'pt-PT', 'Sobre a aplicação do planeamento ao desenvolvimento do projecto;\n\naplicação dos termos contratados com o receptor do software (o cliente);\n\naplicação das medições e métricas planeadas;\n\nmonitorizar e adaptar os planos consoante a evolução do projecto e em alguns casos abandonar o mesmo;\n\norganização e desenvolvimento de relatórios sobre o progresso.');
INSERT INTO TextContents(id, language, textvalue) VALUES (414,'pt-PT', 'Sobre o acompanhamento e avaliação periódica do projecto quanto aos objectivos e satisfação do cliente;\n\ndeterminar a satisfação do cliente quanto aos requisitos;\n\navaliação e revisão da performance dos métodos, ferramentas e recursos aplicados no projecto.');
INSERT INTO TextContents(id, language, textvalue) VALUES (415,'pt-PT', 'Sobre a finalização do projecto na sua totalidade ou numa milestone;\n\ndeterminar a âmbito de “término” quer a nível total ou parcial (e.g. um número pré-acordado de requisitos implementados pode ser um término de fase assim como todos os requisitos acordados e a respectiva documentação pode ser o término total);\n\nas actividades de término de projecto (como o arquivamento de documentação, por quanto tempo manter documentação, actualização de estados de projecto, elaborar documentos de “Lições aprendidas”, etc.).');
INSERT INTO TextContents(id, language, textvalue) VALUES (416,'pt-PT', 'Sobre a aplicação de medições ao nível do projecto, processos e produtos de trabalho;\n\nestabelecimento e incentivo de medições (quais as medições, qual o âmbito, aplicação das medições pelas equipas, etc.);\n\nplaneamento de processos de medições;\n\naplicação de medições nos processos com todos os dados relevantes coleccionados e documentação das mesmas;\n\navaliação das medições e aplicação de correcções ou melhoramentos se necessário.');
INSERT INTO TextContents(id, language, textvalue) VALUES (417,'pt-PT', 'Sobre as ferramentas usadas para auxílio à gestão de um projecto de software (planeamento e acompanhamento do estado de projecto, ferramentas de gestão de risco, ferramentas de comunicação e ferramentas de medição). Estas são também tipicamente software.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (418,'pt-PT', 'Sobre a definição daquilo que é um processo de software e o que o compõe;\n\ninputs requeridos, actividades de trabalho transformativas e os outputs gerados;\n\ndecomposição do processo em unidades de trabalho;\n\ncargos e competências;\n\ntécnicas, ferramentas e notações;\n\ngestão de processos de software, a sua eficiência para um projecto e os impactos que advenham da sua troca por outro.'  );
INSERT INTO TextContents(id, language, textvalue) VALUES (419,'pt-PT', 'Sobre as diferentes categorias de modelos de ciclos de vida do desenvolvimento de software;\n\ncategorias de processos de software, tais com processos primários (para desenvolvimento, operação e manutenção de software), processos de suporte (para gestão das configurações, controlo de qualidade, verificação e validação), processos organizacionais (treino, gestão de infra-estruturas, gestão de portefolio, etc.), processos inter-projectos (transversais a vários projectos);\n\nmodelos de ciclo de vida do software (modelos preditivos ou lineares como o modelo cascata vs modelos iterativos como o modelo espirar vs modelos ágeis de desenvolvimento como scrum);\n\nadaptação de processos de software existentes às necessidades do projecto, organização ou desenvolvimentos tecnológicos.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (420,'pt-PT', 'Sobre o acompanhamento do desempenho dos processos e a sua potencial alteração de modo a melhor responderem às necessidades específicas do projecto ou organização;\n\nos diferentes modelos e métodos (qualitativos que são sujeitos ao julgamento especializado ou quantitativos que são realizados com base em métricas);\n\nmodelos de melhoria iterativa de processos (como por exemplo o Plan-Do-Check-Act);\n\nsistemas de avaliação e pontuação de processos.');
INSERT INTO TextContents(id, language, textvalue) VALUES (421,'pt-PT', 'Sobre as diferentes medidas e técnicas de medição usadas para os processos de software;\n\nmedidas de determinação da eficiência de um processo (como medidas de esforço, horas-homem, dias-homem, custos monetários do processo ou fase de processo) ou do produto em si;\n\na qualidade das medidas a usar para processos e produtos;\n\nrecurso a software com modelos de análise de processos que permitem testar vários parâmetros dos processos usados e determinar possíveis pontos de melhoria;\n\ntécnicas de medição de processos quantitativas (medição directa de número de defeitos, horas-homem, entre outras e respectiva análise por métodos estatísticos) e técnicas qualitativas (como questionários ou entrevistas).');
INSERT INTO TextContents(id, language, textvalue) VALUES (422,'pt-PT', 'Sobre todo o tipo de ferramentas relacionadas com os processos de software, com por exemplo diagramas de BPMN, folhas de cálculo, diagramas de UML ou até software de gestão de projectos.');
INSERT INTO TextContents(id, language, textvalue) VALUES (423,'pt-PT', 'Sobre os princípios gerais de modelação de software, sendo estes “Modelar o essencial”, “Providenciar uma perspectiva sobre o problema” e “Permitir a comunicação eficaz”;\n\nas propriedades de modelos (completude, consistência e exactidão) e expressão de modelos (conceito de entidade e relacionamento e os seus significados de acordo como tipo de modelo);\n\na sintaxe e a semântica das linguagens de modelação (textuais ou gráficas);\n\nos conceitos de pré-condição, pós-condição e invariantes.');
INSERT INTO TextContents(id, language, textvalue) VALUES (424,'pt-PT', 'Sobre os diferentes tipos de modelos usados para software;\n\nmodelos de informação (que identificam e definem um conjunto de conceitos, propriedades, relacionamentos e restrições em entidades de dados);\n\nmodelos comportamentais (identificam e definem as funções do software a ser modelado, os exemplos comuns são máquinas de estado, modelos de controlo de fluxo e modelos de fluxos de dados);\n\nmodelos estruturais (definem a composição lógica ou física das suas diferentes partes, exemplos disso são os diagramas de classe, de objectos e de packages).');
INSERT INTO TextContents(id, language, textvalue) VALUES (425,'pt-PT', 'Sobre as técnicas de inspecção crítica de modelos pelo engenheiro;\n\nanálise de completude;\n\nanálise de consistência;\n\nanálise de exactidão;\n\nrastreamento entre requisitos, modelos e funcionalidade implementada;\n\nanálise de interacção entre modelos e funcionalidade.');
INSERT INTO TextContents(id, language, textvalue) VALUES (426,'pt-PT', 'Sobre as abordagens organizadas e sistemáticas ao desenvolvimento de software para um computador alvo;\n\nMétodos Heurísticos, que são baseados na experiência e largamente praticados pela indústria (tais como métodos de Análise Estruturada que tratam o software como um conjunto hierárquico de funções, métodos de Modelação de Dados que se focam na forma e relacionamento dos dados para organizações cujo princial activo é precisamente os dados e por fim Métodos Orientados aos Objectos, cujo software é um conjunto de objectos que representam o mundo real, encapsulam dados e têm comportamento);\n\nMétodos Formais que utilizam uma notação matemática rigorosa que permite definir, refinar, derivar e verficar software;\n\nMétodos de Prototipagem cuja finalidade é desenvolver uma pequena parte ou parte incompleta do software (normalmente a menos bem compreendida) de modo a poder refiná-la com recurso a input crítico do protótipo;\n\nMétodos Ágeis de desenvolvimento, caracterizados por desenvolvimentos iterativos curtos com alta participação de elementos da equipa (métodos como Extreme Programming, scrum ou Cristal Clear).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (427,'pt-PT', 'Sobre os princípios gerais do que define qualidade para os stakeholders;\n\na instalação de uma cultura e uma ética de trabalho orientada à qualidade;\n\no valor de desenvolver software de qualidade e o impacto que isso tem  nos custos do mesmo;\n\ncaracterísticas e modelos de qualidade a nível de processos e do produto em si;\n\ntécnicas de melhoramento de qualidade (como Plan-Do-Check-Act, kaizen ou Quality Function Deployment);\n\nconceitos de software para sistemas críticos e o impacto que a qualidade tem nestes sistemas.');
INSERT INTO TextContents(id, language, textvalue) VALUES (428,'pt-PT', 'Sobre os processos que asseguram que produtos de software, serviços e modelos de ciclo de vida respondem aos objectivos  organizacionais de qualidade e satisfação de stakeholders;\n\na garantia de qualidade de software (vulgo Quality Assurance);\n\nverificação e validação da qualidade;\n\nrevisão e auditoria de processos e produtos de software.');
INSERT INTO TextContents(id, language, textvalue) VALUES (429,'pt-PT', 'Sobre os problemas práticos que têm um impacto na qualidade do software: o modo como os requisitos influenciam a gestão da qualidade, tais como o tipo de segurança do sistema, o ambiente físico, o orçamento do projecto, o grau de dependência do mesmo (software crítico implica um processo de qualidade elevado);\n\nos diferentes tipos de defeitos do software (erro computacional, erro humano, defeito, falta e falha);\n\nas técnicas de gestão da qualidade do software, como técnicas estáticas (análise de código-fonte, análise de design, métodos formais, etc.), técnicas dinâmicas (execução de código, simulação, debugging), teste de software executado por individuos fora da equipa de desenvolvimento para verificar a conformidade do software à sua especificação;\n\nmedição da qualidade do software (com recurso a métricas de qualidade, análise estatística, análise de tendência, etc.) com a finalidade de ter uma vista geral sobre a qualidade do produto e sobre as partes problemáticas do mesmo.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (430,'pt-PT', 'Sobre as ferramentas de auxílio aos processos de qualidade de software, tais como ferramentas de análise estática de sintaxe e semântica de código (tal como compiladores, Java Mission Control, etc) e ferramentas de análise dinâmica de código (como debuggers).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (431,'pt-PT', 'Sobre a aderência do engenheiro a códigos de ética, conduta profissional e práticas standard estabelecidas pela sua comunidade profissional;\n\nacreditação, certificação e licenciamento;\n\ncódigos de ética e conduta profissional definidos pela sua comunidade profissional (como o código de ética da ACM e IEEE);\n\nas sociedades profissionais e a sua função (por exemplo a IEEE ou a Ordem dos Engenheiros);\n\nos Standards de Engenharia de Software (por exemplo os múltiplos standards da IEEE para Engenharia de Software);\n\no impacto económico do software;\n\nos variados tipos de contratos de trabalho e características;\n\nas questões legais (como marcas registadas, propriedade intelectual, uso justo, cibercrime, direitos de cópia);\n\na criação de documentação e os seus trâmites;\n\nnegociações (de requisitos, funcionalidades, contratos, etc.).');
INSERT INTO TextContents(id, language, textvalue) VALUES (432,'pt-PT', 'Sobre as questões relacionadas com trabalho em equipa e diferentes grupos de individuos como clientes ou colaboradores;\n\nquestões relacionadas com trabalho em grupo (honestidades intelectual, admissão de ignorância, admissão de erros, adaptabilidade);\n\nos factores que afectam a cognição pessoal (volume de dados, complexidade de problemas, incapacidade de exprimir os problemas, incapacidade de consultar com outros, etc.);\n\ncomo lidar com problemas complexos (trabalho em equipa, decomposição de problemas, etc.);\n\ncomo interagir com stakeholders;\n\nlidar com incerteza e ambiguidade nos problemas de desenvolvimento;\n\nlidar com problemas de multiculturalismo de equipas ou clientes.');
INSERT INTO TextContents(id, language, textvalue) VALUES (433,'pt-PT', 'Sobre as capacidades de comunicação orais e de escrita do engenheiro;\n\nleitura de material técnico, compreensão e resumo;\n\nhabilidades de escrita de documentos relevantes aos processos de software (requisitos, documentação de software, contratos, etc.);\n\ncomunicação eficaz com equipas ou grupos;\n\nhabilidades de criar e executar apresentações (de ideias, de produtos, pitches, etc.).');
INSERT INTO TextContents(id, language, textvalue) VALUES (434,'pt-PT', 'Sobre os conceitos base de Economia;\n\nfinanças e contabilidade;\n\ncontrolo de custos;\n\ncash flow;\n\nprocessos de tomada de decisões (orientada à Economia);\n\nconceito de valorização;\n\ninflação e depreciação;\n\ntaxação (impostos);\n\nvalor temporal do dinheiro (a oscilação do valor do dinheiro ao longo do tempo);\n\neficiência económica;\n\neficácia;\n\ndefinição de produtividade.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (435,'pt-PT', 'Sobre os conceitos económicos aplicados à actividade do software ao longo do seu ciclo de vida;\n\nconceitos de produto, projecto, programa, portefolio, ciclo de vida de um produto, ciclo de vida de um projecto, propostas e tipos de propostas;\n\ndecisões de investimento;\n\nhorizonte de planeamento (sobre os custos económicos relacionados com investimentos num determinado prazo, como o tempo de vida de um produto de sotware e o seu impacto económico numa empresa);\n\nconceito de preço e estabelecimento de políticas de preços;\n\nconceito de custo e gestão de custos;\n\nmedição de performance económica;\n\ngestão do valor ganho (técnica económica que permite um controlo eficaz de custos comparando o valor já criado com o orçamento projectado e com o custo calendarizado até esse momento);\n\ndecisões de terminação de um produto baseadas em economia;\n\ndecisões de troca (troca de um software antiquado por um novo em vez de o manter) ou descontinuação (de um produto ou de actividade como um todo).');
INSERT INTO TextContents(id, language, textvalue) VALUES (436,'pt-PT', 'Sobre os conceitos de objectivo económico, estimativas e planeamento;\n\ntécnicas de estimação (como analogias, estimação por partes, métodos estatísticos, etc.);\n\nreagir a incerteza (como utilizar estimativas de diversos âmbitos, adiar decisões finais, etc.);\n\ntécnicas de prioritização;\n\ntécnicas de tomada decisões sob risco (como árvores de decisão, análise de Monte Carlo, etc.);\n\ntécnicas de tomada de decisões sob incerteza (como Regra de Laplace, Regra de Hurwicz, etc.).');
INSERT INTO TextContents(id, language, textvalue) VALUES (437,'pt-PT', 'Sobre os diferentes métodos de análise ecónomica aplicáveis dependendo do tipo de organização, finalidade ou objectivos do software: análise de decisão de organizações com fins lucrativos;\n\nmínimo rácio de retorno aceitável (critério de escolha de investimentos baseado num retorno percentual mínimo);\n\nretorno de investimento (rácio entre o valor ganho relativamente ao investido);\n\nretorno do capital aplicado;\n\nanálise custo-benefício;\n\nanálise custo-efectividade;\n\nanálise de break-even (igualdade entre custos e rendimentos);\n\ncaso de negócio (apresentação estruturada e consolidada sobre uma proposta de negócio a partir de diferentes perspectivas de decisão como custos, benefícios, risco, etc.);\n\navaliação a multiplos atributos (a nível de outras propriedades que não simplesmente dinheiro);\n\nanálise de optimização.');
INSERT INTO TextContents(id, language, textvalue) VALUES (438,'pt-PT', 'O princípio do “Suficientemente Bom” (quando o custo de adicionar uma nova funcionalidade - ou investir em algo não essencial - não pode ser medido este princípio é um indicador de quando o estado desta funcionalidade é suficientemente boa para ser entregue);\n\nmercados sem fricção (sempre que existem entraves a uma competição perfeita, sejam custos de transporte, estabelecimentos, proteccionismo e, particularmente no software, não-neutralidade de internet ou limitação de venda de aplicações em lojas especificas);\n\necossistemas (quando as qualidades ou atributos de um produto são melhoradas devido à existência de terceiros que de alguma forma interagem com  produto, como por exemplo, uma aplicação que é integrada ou configurada à medida por uma empresa externa);\n\noffshoring (o desenvolvimento de uma actividade económica fora do território de operação da organização por razões de custos ou de défices de qualidade) e outsourcing (a execução de tarefas para uma organização por uma entidade externa quando essas actividades poderiam ser feitas pela organização).');
INSERT INTO TextContents(id, language, textvalue) VALUES (439,'pt-PT', 'Sobre a definição de resolução de problemas;\n\nformulação de problemas reais;\n\nanálises de problemas;\n\ndesenhar uma estratégia de procura de soluções;\n\nresolução de problemas com recurso a programas de computadores.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (440,'pt-PT', 'Sobre os conceitos inerentes à abstracção - genericamente definida como “o processo e o resultado da generalização através da redução da informação a um conceito, um problema ou um fenómeno observável de modo a permitir o foco no panorâma geral;\n\nos níveis de abstracção;\n\nconceito de encapsulamento;\n\nhierarquia de abstracções.');
INSERT INTO TextContents(id, language, textvalue) VALUES (441,'pt-PT', 'Sobre os conceitos-base de programação – a criação de programas de computador com recurso a código;\n\no processo de programação (desenho, escrita, teste, debug e manutenção);\n\nas características dos diferentes paradigmas de programação (não-estruturada, estruturada, procedimental, imperativa, orientada aos objectos, funcional, orientada aos aspectos, etc).');
INSERT INTO TextContents(id, language, textvalue) VALUES (442,'pt-PT', 'Sobre as linguagens em que é possível escrever código com a finalidade de criar programas para um computador;\n\na sintaxe e semântica de uma linguagem;\n\nlinguagens de baixo nível, linguagens de alto nível;\n\ndiferenças entre linguagens declarativas e imperativas.');
INSERT INTO TextContents(id, language, textvalue) VALUES (443,'pt-PT', 'Sobre o processo de encontrar e corrigir problemas no software escrito;\n\nos diferentes tipos de erros (de sintaxe, erros lógicos, erros de dados, etc.);\n\ntécnicas de debugging (um passo de cada vez, recurso a breakpoints, recurso a pontos de observação, etc.);\n\nferramentas de debugging (como debuggers ou ferramentas de análise de código estático).');
INSERT INTO TextContents(id, language, textvalue) VALUES (444,'pt-PT', 'Sobre a organização e formas de expressão de dados para uso por programas;\n\ntipos de estruturas de dados (listas, filas, pilhas, tabelas, tabelas de hash, etc.);\n\nas diferentes operações em diferentes estruturas de dados (CRUD – create,read,update e delete, operações de encontro de elementos, travessia, ordenação).');
INSERT INTO TextContents(id, language, textvalue) VALUES (445,'pt-PT', 'Sobre a escrita organizada de programas em algoritmos – uma estratégia definida numa série de passos com vista à resolução de um problema;\n\nos atributos de algoritmos (modularidade, funcionalidade, robustez, simplicidade, etc.);\n\nanálise de algoritmos (análise do pior caso, análise do caso médio, notação Big-O,etc.);\n\nestratégias de design de algoritmos (força-bruta, divide and conquer, algoritmos gulosos, programação dinâmica, etc.);\n\nestratégias de análise de algoritmos (contagem de passos, análise assimptótica, análise probabilística, etc.).');
INSERT INTO TextContents(id, language, textvalue) VALUES (446,'pt-PT', 'Sobre a definição de um sistema;\n\nas propriedades de um sistema;\n\npropriedades emergentes de um sistema;\n\nengenharia de sistemas e bases sobre um sistema de computadores.');
INSERT INTO TextContents(id, language, textvalue) VALUES (447,'pt-PT', 'Sobre os quatro níveis em que se divide a organização de um computador (as macro-arquitecturas, micro-arquitecturas, circuitos lógicos e dispositivos);\n\nsistemas digitais (baseados em sinais e código binário);\n\nlógica digital (as operações sobre números binários como AND, OR, etc.);\n\na expressão de dados binários num computador;\n\na unidade de processamento central e o seu funcionamento (CPU);\n\na organização de memória;\n\no funcionamento de dispositivos de I/O.');
INSERT INTO TextContents(id, language, textvalue) VALUES (448,'pt-PT', 'Sobre o que é um compilador;\n\nas diferenças entre compiladores e interpretadores;\n\no processo de compilação.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (449,'pt-PT', 'Sobre os conceitos de um sistemas operativo;\n\nas tarefas de um sistemas operativo;\n\nos diferentes tipos de sistemas operativos (batch, sistemas em time-sharing, sistemas em tempo real, sistemas operativos distribuídos, sistemas embebidos, sistema operativo de servidor, sistema operativo móvel, sistema operativo de computador pessoal, etc.).');
INSERT INTO TextContents(id, language, textvalue) VALUES (450,'pt-PT', 'Sobre os conceitos de uma base de dados;\n\nentidade e esquema;\n\nos diferentes sistemas de gestão de bases de dados (Oracle, MS SQL Server, Postgre, etc.);\n\nlinguagem de interpelação de bases de dados – como SQL – as suas características e como a usar;\n\norganização e manipulação de um sistema de gestão de bases de dados;\n\nmineração de dados (a descoberta de padrões e relações pouco óbvias entre os dados).');
INSERT INTO TextContents(id, language, textvalue) VALUES (451,'pt-PT', 'Sobre os diferentes tipos de redes (rede pessoal, rede local, wide area network, internet, etc.);\n\nos conceitos básicos de redes (os seus componentes e as diferentes funções destes);\n\nos protocolos e standards de redes (pilha OSI e seus componentes como TCP/IP, UDP, Bluetooth, etc.);\n\na Internet;\n\na Internet das Coisas;\n\nredes virtuais privadas (vulgo VPN).');
INSERT INTO TextContents(id, language, textvalue) VALUES (452,'pt-PT', 'Sobre os conceitos básicos sobre o que é computação paralela e computação distribuída;\n\nas diferenças entre estes dois paradigmas;\n\nmodelos de computação paralela (memória partilhada, passagem de mensagens, etc.);\n\nos principais problemas na computação distribuída.');
INSERT INTO TextContents(id, language, textvalue) VALUES (453,'pt-PT', 'Sobre os problemas típicos que advêm da utilização de software pelos utilizadores humanos;\n\nconsiderações sobre o I/O (como mostrar o output a utilizadores, qual o formato mais adequado, qual a maneira mais agradável de apresentar, etc.);\n\nmensagens de erro (como explicar claramente o que aconteceu e como resolver, qual o tipo de linguagem, etc.);\n\nrobustez de software (como o software tolera o uso incorrecto por pessoas, como recuperar de maneira a não desincentivar o uso por pessoas, etc.).');
INSERT INTO TextContents(id, language, textvalue) VALUES (454,'pt-PT', 'Sobre os problemas comuns que advêm de o software ser construído e mantido por humanos e como a sua escrita afecta estas actividades;\n\ncomo estruturar o código para facilitar compreensão;\n\ncomo escrever comentários que facilitem o trabalho dos responsáveis presentes e futuros do software.');
INSERT INTO TextContents(id, language, textvalue) VALUES (455,'pt-PT', 'Sobre as práticas e regras seguidas com a finalidade de tornar o software mais seguro em todas as suas fases, quer a nível de requisitos, como construção e teste.');
INSERT INTO TextContents(id, language, textvalue) VALUES (456,'pt-PT', 'Sobre os conceitos matemáticos e lógicos sobre os conjuntos (colecção de elementos), as suas propriedades e as suas operações (intesecção, união, produto cartesiano, etc.);\n\nsobre relacionamentos (associações entre dois conjuntos de informação);\n\nsobre funções matemáticas (uma relação de comportamento conhecido que exprime o mapeamento entre elementos de domínios).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (457,'pt-PT', 'Sobre a lógica proposicional, a sua terminologia e leis (variáveis Booleanas, operações booleanas, Leis de Identidade, Leis Distributivas, Leis de De Morgan, etc.): a lógica de Predicados, as suas propriedades, terminologia e leis.');
INSERT INTO TextContents(id, language, textvalue) VALUES (458,'pt-PT', 'Sobre os argumentos que estabelecem rigorosamente a verdade de uma afirmação;\n\nos métodos de prova de um teorema (provas por contradição, provas por indução, etc.).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (459,'pt-PT', 'Sobre as acções que permitem encontrar o número de elementos num conjunto finito de elementos e suas regras (regra da soma, regra do produto, etc.) e princípios;\n\nprincípio da inclusão-exclusão;\n\ndefinição de recursividade, aleatoriedade, probabilidade, permutação e combinação.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (460,'pt-PT', 'Sobre os diferentes tipos de grafos (grafos direccionados, grafos não-direccionados, etc.), a sua terminologia (vértices, nós, peso, arestas, etc.) e operações (travessia, conexões, etc.);\n\nos diferentes tipos de árvores, as suas propriedades e operações (travessias, adições, etc.).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (461,'pt-PT', 'Sobre a descrição matemática da aleatoriedade a sua terminologia, propriedades e operações.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (462,'pt-PT', 'Sobre as máquinas de estados finitos (a abstracção matemática composta por um número finito de estados e as transições entre estes), a sua terminologia e operações.');
INSERT INTO TextContents(id, language, textvalue) VALUES (463,'pt-PT', 'Sobre as gramáticas de linguagens formais, a sua terminologia (vocabulário, frase, terminais, símbolo, etc.);\n\nos tipos de gramáticas existentes (gramática livre de contexto, gramática sensível ao contexto, etc.).');
INSERT INTO TextContents(id, language, textvalue) VALUES (464,'pt-PT', 'Sobre os problemas que recorrem na representação de números;\n\no carácter finito da precisão de representação de números num computador;\n\ntécnicas de arredondamentos;\n\nalgarismos significativos;\n\ntipos de erros (relativos, absolutos).');
INSERT INTO TextContents(id, language, textvalue) VALUES (465,'pt-PT', 'Sobre os números;\n\nos tipos de números (inteiro, racional, real, complexo, imaginário etc.);\n\nterminologia e operações (divisão, mod, módulo);\n\nos números primos, a sua utilidade a sua aplicação (como criptografia).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (466,'pt-PT', 'Sobre as estruturas algébricas (definidas como conjuntos sujeitos a algumas operações e que satisfaçam um número de axiomas);\n\nsobre grupos, as suas propriedades (associação, identidade, fecho, etc.) e tipos (monóide, subgrupo);\n\nanéis (um conjunto com duas operações binárias - normalmente adição e multiplicação - onde cada operação combina dois elementos para formar um terceiro).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (467,'pt-PT', 'Sobre como propôr soluções ou modelos de solução e realizar experiências para resolver problemas;\n\ncriação de experiências controladas (formular hipóteses, criar pré-condições, observar variáveis, relacionamento entre resultados, etc.);\n\nestudo observacional com recurso a contextualização;\n\nestudo retrospectivo, também conhecido como estudo histórico (análise de  dados históricos com a finalidade de encontrar relacionamentos entre dados e prever eventos futuros.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (468,'pt-PT', 'Sobre a capacidade de atingir resultados generalizaveis através do estudo de uma população limitada de amostras de resultados prévios (i.e. com recurso a estatística);\n\nos conceitos estatísticos de amostra, população, unidade de análise, variável aleatória, evento, distribuição de variáveis, estimação, tipos de estimativas, propriedades de estimadores e testes de hipóteses estatísticas;\n\ncorrelação e regressão.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (469,'pt-PT', 'Sobre a problemática da medição no software e as suas diferenças fundamentais para outros atributos de medição mais simples;\n\nos conceitos de níveis (ou escalas) de medição (nominal, ordinal, intervalar e por rácios);\n\nmedidas directas versus medidas indirectas;\n\nconfiabilidade e validade de uma medida;\n\navaliação de confiabilidade de medidas.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (470,'pt-PT', 'Sobre os desafios que a elaboração de uma solução estruturada para um problema de engenharia representam para o engenheiro, sendo que o desenho neste caso não tem nada que ver com o seu conceito tradicional - a representação gráfica - mas sim com o planeamento ou arquitectura de uma solução para um problema, o seu processo e as suas fases (definição de um problema, obtenção de informação pertinente, geração de múltiplas soluções, análise e selecção de uma solução, implementação da solução).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (471,'pt-PT', 'Sobre os conceitos e técnicas de modelação (o processo de representação de parte ou totalidade de um sistema recorrendo a abstracções) e os seus diferentes tipos (icónico, analógico e simbólico);\n\no conceito de simulação (condução de experiências em modelos com a finalidade de melhorar a compreensão sobre o mesmo) e a sua construção;\n\no conceito de protótipo (uma representação inicial, parcial ou incompleta de um sistema) e a sua utilização.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (472,'pt-PT', 'Sobre o conceito de standard (requisito, especificação, guia ou característica que deve ser observado por engenheiros de modo que produtos, processos e materiais tem níveis aceitáveis de qualidade);\n\nos diferentes tipos de standards existentes;\n\na conformidade mensurável com standards;\n\nas diferentes entidades que produzem standards internacionais de engenharia.' );
INSERT INTO TextContents(id, language, textvalue) VALUES (473,'pt-PT', 'Sobre o processo de investigação e identificação de como e porque aconteceu um evento indesejável e sua causa de raíz (i.e. o que deu origem ao evento);\n\nas principais técnicas para conduzir este tipo de análise (métodos de revisão, diagramas de estado presente vs estado desejável, revisão por terceiros, árvores de falha, etc.);\n\nas ferramentas de auxílio à análise (diagramas de causa-efeito, histogramas, diagramas de Pareto, etc.).' );
INSERT INTO TextContents(id, language, textvalue) VALUES (474,'pt-PT', 'Para qualquer tópico do programa que não encaixa em qualquer das Áreas do Conhecimento.' );
