INSERT INTO TextContents(id, language, textValue) VALUES (3036, 'pt-PT', 'Campo electrostático no vácuo. Lei de Coulomb. Princípio de sobreposição. Nocão de campo e de potencial. Dipolo eléctrico. Lei de Gauss. Condensador');
