INSERT INTO TextContents(id, language, textValue) VALUES (3038, 'pt-PT', 'Corrente eléctrica estacionária. Densidade e intensidade de corrente. Equação da continuidade da carga. Lei de Ohm. Lei de Joule. Leis de Kirchoff. Circuito RC');
INSERT INTO TextContents(id, language, textValue) VALUES (3039, 'pt-PT', 'Campo magnético no vácuo. Lei de Biot-Savart. Lei de Ampère. Força de Lorentz. Fluxo magnético. Coeficientes de indução. Bobina.');
INSERT INTO TextContents(id, language, textValue) VALUES (3040, 'pt-PT', 'Campo magnético na matéria. Magnetização. Diamagnetismo, paramagnetismo e ferromagnetismo. Energia em magnetostática');
