INSERT INTO TextContents(id, language, textValue) VALUES (3058, 'pt-PT', 'Variáveis aleatórias: Função de distribuição. Variáveis aleatórias discretas e contínuas. Valor esperado, variância e outros parâmetros. Distribuições discretas e contínuas usuais.');
INSERT INTO TextContents(id, language, textValue) VALUES (3059, 'pt-PT', 'Distribuições conjuntas e complementos: Distribuições conjunta, marginais e condicionadas. Independência. Correlação. Aproximações entre distribuições. Teorema do limite central. Lei dos Grandes Números.');
INSERT INTO TextContents(id, language, textValue) VALUES (3060, 'pt-PT', 'Amostragem e estimação pontual: Estatística descritiva versus indutiva. Amostra aleatória. Estatísticas. Estimação pontual. Propriedades dos estimadores. Método da máxima verosimilhança. Distribuições amostrais da média e variância.');
