INSERT INTO TextContents(id, language, textValue) VALUES (3063, 'pt-PT', 'Regressão Linear Simples: Estimação pelo método dos mínimos quadrados e respectivas propriedades. Inferência no modelo de regressão linear simples. Coeficiente de determinação e análise empírica de resíduos.');
