INSERT INTO TextContents(id, language, textValue) VALUES (3101, 'pt-PT', 'Matemática para CG (inclui trigonometria e matrizes): Revisão de conceitos matemáticos relevantes para computação gráfica, como trigonometria, geometria analítica e matrizes.');
INSERT INTO TextContents(id, language, textValue) VALUES (3102, 'pt-PT', 'Introdução ao three.js: Apresentação da biblioteca three.js para criação de aplicações web 3D.');
INSERT INTO TextContents(id, language, textValue) VALUES (3103, 'pt-PT', 'Transformações Geométricas: Estudo de transformações geométricas em 2D e 3D, incluindo translação, rotação, escala e projeção, e sua implementação em three.js.');
INSERT INTO TextContents(id, language, textValue) VALUES (3104, 'pt-PT', 'Grafos de cena: Abordagem da organização de objetos em uma cena 3D utilizando grafos de cena.');
