INSERT INTO TextContents(id, language, textValue) VALUES (3106, 'pt-PT', 'Programação baseada em acontecimentos, animação e colisões: Desenvolvimento de aplicações interativas com three.js, incluindo animação e detecção de colisões.');
INSERT INTO TextContents(id, language, textValue) VALUES (3107, 'pt-PT', 'Pipeline Gráfico: Estudo do processo de renderização de imagens 3D, incluindo visualização, projeção, iluminação, sombreamento e recorte.');
INSERT INTO TextContents(id, language, textValue) VALUES (3108, 'pt-PT', 'Operações sobre fragmentos: Memórias-tampão e mapeamento de texturas: Otimização de renderização e aplicação de texturas em objetos 3D.');
INSERT INTO TextContents(id, language, textValue) VALUES (3109, 'pt-PT', 'Novas arquitecturas hardware/software em Computação Gráfica: Tendências em hardware e software para computação gráfica, como Realidade Virtual e Realidade Aumentada.');
INSERT INTO TextContents(id, language, textValue) VALUES (3110, 'pt-PT', 'Introdução à Inteligência Artificial: O que é a Inteligência Artificial e como se diferencia da informática tradicional; Uma breve história da Inteligência Artificial.');
INSERT INTO TextContents(id, language, textValue) VALUES (3111, 'pt-PT', 'Agentes inteligentes: O conceito de racionalidade; Ambientes e tipos de agentes.');
INSERT INTO TextContents(id, language, textValue) VALUES (3112, 'pt-PT', 'Resolução de problemas por procura em espaços de estados: Métodos de procura não-informados e informados; Estudo de heurísticas.');
INSERT INTO TextContents(id, language, textValue) VALUES (3113, 'pt-PT', 'Procura com adversários: Jogos.');
INSERT INTO TextContents(id, language, textValue) VALUES (3114, 'pt-PT', 'Problemas de satisfação de restrições: Procura com retrocesso, procura local e estrutura de problemas.');
INSERT INTO TextContents(id, language, textValue) VALUES (3115, 'pt-PT', 'Representação de Conhecimento: Engenharia de ontologias; Categorias e objetos; Ações, situações e eventos.');
INSERT INTO TextContents(id, language, textValue) VALUES (3116, 'pt-PT', 'Planeamento de ações: Procura em espaço de estados, planeamento de ordem parcial, grafos de planeamento e planeamento com lógica proposicional.');
INSERT INTO TextContents(id, language, textValue) VALUES (3117, 'pt-PT', 'Aprendizagem: Aprendizagem indutiva, árvores de decisão, "ensemble learning" e teoria da aprendizagem computacional.');
INSERT INTO TextContents(id, language, textValue) VALUES (3118, 'pt-PT', 'Comunicação: Gramáticas e "parsing".');
INSERT INTO TextContents(id, language, textValue) VALUES (3119, 'pt-PT', 'Linguagens de programação usadas em Inteligência Artificial: Introdução ao LISP, uma linguagem funcional.');
INSERT INTO TextContents(id, language, textValue) VALUES (3120, 'pt-PT', 'Introdução e revisão dos conceitos básicos: Revisão da organização interna e funcionamento de um computador; Perspectiva da evolução recente e futura.');
INSERT INTO TextContents(id, language, textValue) VALUES (3121, 'pt-PT', 'Organização do computador: Processadores CISC vs RISC; Condicionantes da evolução tecnológica dos computadores; Geração de código, papel dos compiladores; Organização dos dados em memória; Representação de números, formato IEEE 754; Métricas de desempenho.');
INSERT INTO TextContents(id, language, textValue) VALUES (3122, 'pt-PT', 'Funcionamento de um processador RISC: Organização; Processamento em pipeline; Conflitos no pipeline de dados, controlo e estruturais; Optimização do código.');
INSERT INTO TextContents(id, language, textValue) VALUES (3123, 'pt-PT', 'Sistema de memória: Hierarquia de memória; Memória primária; Arquitetura das memórias cache; Optimização de programas; Gestão de memória, ligação ao sistema operativo; Mecanismos eficientes de tradução de endereços; Funcionamento integrado do sistema de memória.');
INSERT INTO TextContents(id, language, textValue) VALUES (3124, 'pt-PT', 'Sistema de entradas e saídas: Interface analógico-digital; Desempenho na comunicação com periféricos; Características de barramentos padrão; Interface de periféricos com a memória, processador e sistema operativo; Interrupções, exceções e traps; DMA; Compressão de dados; Fiabilidade de dados: paridade, ECC, RAID.');
INSERT INTO TextContents(id, language, textValue) VALUES (3125, 'pt-PT', 'Arquiteturas avançadas: Processadores multiple-issue: superescalares e VLIWs; Arquiteturas paralelas, taxonomia de Flynn; Multiprocessadores: SMP e NUMA; Coerência de memória; Sincronismo; Clusters.');
INSERT INTO TextContents(id, language, textValue) VALUES (3126, 'pt-PT', 'Introdução: Redes de computadores e a Internet; Tecnologias de comutação; Arquitetura em camadas; Protocolos; Serviços; Medidas de desempenho.');
INSERT INTO TextContents(id, language, textValue) VALUES (3127, 'pt-PT', 'Camada de aplicação: A Web e HTTP; FTP; SMTP; DNS; A interface de sockets.');
INSERT INTO TextContents(id, language, textValue) VALUES (3128, 'pt-PT', 'Camada de transporte: Multiplexagem e desmultiplexagem; UDP; Transferência fiável de dados; Stop-and-wait, go-back-N e selective Repeat; TCP; Controlo de congestão.');
INSERT INTO TextContents(id, language, textValue) VALUES (3129, 'pt-PT', 'Camada de rede: Encaminhamento por estado-da-ligação; Encaminhamento por vector-distância; IP; CIDR; RIP; OSPF; BGP; IPv6; VPNs; NAT.');
INSERT INTO TextContents(id, language, textValue) VALUES (3130, 'pt-PT', 'Camada da ligação de dados: Detecção e correcção de erros; Acesso múltiplo; ALOHA; CSMA; Token-ring; WLANs e IEEE 802.11; ARP; Hubs e comutadores; PPP; ATM.');
INSERT INTO TextContents(id, language, textValue) VALUES (3131, 'pt-PT', 'Aplicações multimedia: Streaming áudio e vídeo; Telefonia pela Internet; Qualidade de serviço; Escalonamento de tráfego.');
INSERT INTO TextContents(id, language, textValue) VALUES (3132, 'pt-PT', 'Segurança em redes: Criptografia de chave simétrica, DES e AES; Criptografia de chave pública, Diffie-Hellman e RSA; Confidencialidade; Autenticação; Integralidade; PGP; SSL; IPSec.');
INSERT INTO TextContents(id, language, textValue) VALUES (3133, 'pt-PT', 'Conceitos fundamentais: Definições de "sistema" e de "sistema de sistemas"; Conceitos de contexto e de arquitectura de sistema, "stakeholders", “concern”, “viewpoint”, “view” e modelos.');
INSERT INTO TextContents(id, language, textValue) VALUES (3134, 'pt-PT', 'Técnicas de representação de contextos, com destaque para a técnica de mapas conceptuais.');
INSERT INTO TextContents(id, language, textValue) VALUES (3135, 'pt-PT', 'Conceitos, motivações para a MDA (Model Driven Architecture) e MDE (Model Driven Engineering).');
INSERT INTO TextContents(id, language, textValue) VALUES (3136, 'pt-PT', 'Entidades de normalização de referência (ISO, ITU, IEEE, W3C, IETF, INCOSE, NISO, IPQ, OASIS, The Open Group, OMG).');
INSERT INTO TextContents(id, language, textValue) VALUES (3137, 'pt-PT', 'Engenharia de Requisitos: conceitos, processos e técnicas fundamentais: Requisitos como “concerns” e constrangimentos aos sistemas.');
INSERT INTO TextContents(id, language, textValue) VALUES (3138, 'pt-PT', 'Os processos de levantamento, negociação, validação e documentação de requisitos. Ferramentas de gestão de requisitos. Técnicas para levantamento de requisitos. Regras para expressão de requisitos bem formados e SMART (Specific, Measurable, Achievable, Realisable, Traceable). Técnicas de rastreabilidade.');
INSERT INTO TextContents(id, language, textValue) VALUES (3139, 'pt-PT', 'Requisitos funcionais e Requisitos não funcionais.');
INSERT INTO TextContents(id, language, textValue) VALUES (3140, 'pt-PT', 'Conceitos de vistas de sistemas “black box” e “white box”. Casos de uso e cenários de casos de uso como técnicas de engenharia de requisitos (UML e SysML).');
INSERT INTO TextContents(id, language, textValue) VALUES (3141, 'pt-PT', 'Contexto de sistema e questões éticas, sociais e políticas. Dimensões éticas e morais de análise de sistemas. Códigos de ética em engenharia. (IEEE, ACM e OE)');
INSERT INTO TextContents(id, language, textValue) VALUES (3142, 'pt-PT', 'Análise e Modelação Orientada a Objectos - Modelos de Estrutura: Modelação orientada a objectos: conceitos; vistas de estrutura e de comportamento.');
INSERT INTO TextContents(id, language, textValue) VALUES (3143, 'pt-PT', 'Sistemas físicos e sistemas lógicos. Sistemas de informação como sistemas lógicos.');
INSERT INTO TextContents(id, language, textValue) VALUES (3144, 'pt-PT', 'Análise de universos de discurso e desenvolvimento de modelos de domínio em UML.');
