INSERT INTO TextContents(id, language, textValue) VALUES (3146, 'pt-PT', 'Diagramas de componentes e de instalação em UML.');
INSERT INTO TextContents(id, language, textValue) VALUES (3147, 'pt-PT', 'Modelação de interfaces e Diagramas de objectos.');
INSERT INTO TextContents(id, language, textValue) VALUES (3148, 'pt-PT', 'Modelos em SysML.');
INSERT INTO TextContents(id, language, textValue) VALUES (3149, 'pt-PT', 'Análise e Modelação Orientada a Objectos - Modelos de Comportamento: Vistas de comportamento “black box” (casos de uso) e “white box” (eventos, fluxos e mensagens).');
INSERT INTO TextContents(id, language, textValue) VALUES (3150, 'pt-PT', 'Técnicas UML para desenvolvimento de vistas de comportamento: diagramas de mensagens (sequência, comunicação, temporais; interacção geral), de actividade, e de máquinas de estado.');
INSERT INTO TextContents(id, language, textValue) VALUES (3151, 'pt-PT', 'Sistemas de Informação - Análise e Modelação de Processos de Negócio O processo como transformação de inputs em outputs com ênfase no produto final e produção de valor.');
