INSERT INTO TextContents(id, language, textValue) VALUES (3160, 'pt-PT', 'Linguagens livres de contexto: gramaticas livres de contexto, árvores de derivação, autómatos finitos com pilha, analisadores sintacticos descendendes LL(1), e ascendendes LR(0)/ SLR(1)/ LALR(1).');
